const Header = ({ onMenuClick, onLogoClick }: HeaderProps) => (
    <header className="app-header app-container">
        <a href="#" className="logo-button" onClick={(e) => { e.preventDefault(); onLogoClick(); }}>
            <h1 className="logo">YB</h1>
        </a>
        <button className="menu-button" onClick={onMenuClick} aria-label="Open menu">
            <Icon name="menu" />
        </button>
    </header>
);

const NavSidebar = ({ isOpen, onClose, currentView, onViewChange }: NavSidebarProps) => (
    <>
        <div className={`nav-sidebar-overlay ${isOpen ? 'open' : ''}`} onClick={onClose}></div>
        <nav className={`nav-sidebar ${isOpen ? 'open' : ''}`} aria-hidden={!isOpen}>
            <div className="nav-header">
                <h2>Navigation</h2>
                <button className="close-button" onClick={onClose} aria-label="Close menu">
                    <Icon name="close" />
                </button>
            </div>
            <ul className="nav-list">
                {navItems.map(item => (
                    <li key={item.name} className={`nav-item ${currentView === item.name ? 'active' : ''}`}>
                        <button className="nav-link" onClick={() => onViewChange(item.name)}>
                            <Icon name={item.icon} />
                            <span>{item.name}</span>
                        </button>
                    </li>
                ))}
            </ul>
        </nav>
    </>
);

